import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    super();
    console.log("hi i m constructor!!!");
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
  }
  async componentDidMount() {
    console.log("cdm");
    let url =
      "https://newsapi.org/v2/everything?q=tesla&from=2022-05-27&sortBy=publishedAt&apiKey=b1fc155ad9fd413183b57d759c0c3daa";
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles });
  }
  handlePrevClick = () => {
    console.log("this is prev button");
  };
  handleNextClick = () => {
    console.log("this is next button");
  };
  render() {
    return (
      <div className="container my-3">
        <h3>NEWS MONKEY</h3>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title ? element.title.slice(0, 45) : ""}
                  description={
                    element.description ? element.description.slice(0, 88) : ""
                  }
                  imgUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button type="button" class="btn btn-dark" onClick={handlePrevClick}>
            {" "}
            &larr; Previous
          </button>
          <button type="button" class="btn btn-dark" onClick={handleNextClick}>
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
